import React from 'react';
import './Form.scss';

class Form extends React.Component {
    constructor(props) {
        super(props);
        //initial state of the component
        this.state = {
            url: 'nothing to see here',
            method:''
        };
    }

    handleSubmit = () => {

        const url = document.querySelector('#urlInput').value;
        const radioButtons = document.getElementsByName('method');
        for (let index = 0; index < radioButtons.length; index++) {
            if(radioButtons[index].checked){
                this.state.method = radioButtons[index].value;
                break;
            }
        }
        this.setState({ url, method });
        console.log(this.state);

    }

    render() {
        return (
            <main>
                <div>
                    <input name="url" type="text" id="urlInput" placeholder="URL" required />
                    <button onClick={this.handleSubmit}>Go</button>
                    <input type="radio" name="method" value="get" required />
                    <label >GET</label>
                    <input type="radio" name="method" value="post" />
                    <label >POST</label>
                    <input type="radio" name="method" value="put" />
                    <label >PUT</label>
                    <input type="radio" name="method" value="delete" />
                    <label >DELETE</label>
                </div>

                <div>
                    <pre>{this.state.method}    {this.state.url}</pre>
                </div>
            </main>

        );
    }
}

export default Form;
