import React from 'react';
import './Form.scss';

class Form extends React.Component {
    constructor(props) {
        super(props);
        //initial state of the component
        this.state = {
            words: 'nothing to see here',
        };
    }

    handleSubmit = (event) => {
        // event.preventDefault();
        const url = document.querySelector('#urlInput').value;

        console.log(document.getElementsByName('method')[9].value)
        this.setState({ url });
    }

    render() {
        return (
            <main>
                <div>
                    <input name="url" type="text" id="urlInput" placeholder="URL" required />
                    <button onClick={this.handleSubmit}>Go</button>
                    <input type="radio" name="method" value="get" required />
                    <label >GET</label>
                    <input type="radio" name="method" value="post" />
                    <label >POST</label>
                    <input type="radio" name="method" value="put" />
                    <label >PUT</label>
                    <input type="radio" name="method" value="delete" />
                    <label >DELETE</label>
                </div>

                <div>
                    <p>{this.state.url}</p>
                </div>
            </main>

        );
    }
}

export default Form;
